<?php
$sale_rent = $item->getSaleRent();
$location = $item->getLocationInfo();
$static_map_location = '';

foreach (['subarea', 'area', 'district', 'country'] as $location_key) {
    if (!empty($location[$location_key])) {
        $static_map_location .= (!empty($static_map_location) ? ',' : '') . $location[$location_key];
    }
}

$url = $item->getSavedSearchUrl();
$extraUrlParams = [
    'area' => $location['area'] ?? '',
    'subarea' => $location['subarea'] ?? '',
    'sale_rent' => $sale_rent,
];

$number_fields = [
    'list_selling_price_amount_min',
    'list_selling_price_amount_max',
    'list_rental_price_amount_min',
    'list_rental_price_amount_max',
];

$fields_mapping = \App\Utils\Config::getListByThemeByKey('field_mapping', 'lead_list', $site_data['theme']);
foreach ($fields_mapping as $filter_field_name => $lead_field_name) {
    $field_value = $item->get($lead_field_name);
    if (in_array($filter_field_name, $number_fields)) {
        $field_value = (int)$field_value;
    }

    if (empty($field_value)) {
        continue;
    }

    $extraUrlParams[$filter_field_name] = $field_value;
}

if (!empty($extraUrlParams['post_code']) && is_array($extraUrlParams['post_code'])) {
    $extraUrlParams['post_code'] = current($extraUrlParams['post_code']);
}

unset($extraUrlParams['sale_rent']);

$sort = $item->get('sorting_expression');
if (!empty($sort)) {
    $sort_mapping = array_flip(\App\Utils\Config::getListByThemeByKey('field_mapping', 'sort_list', $site_data['theme']));
    if (!empty($sort_mapping[$sort])) {
        $extraUrlParams['sort'] = $sort_mapping[$sort];
    }
}

$property_type = $item->get('enquiry_type');
$property_subypes = $item->get('enquiry_subtypes');
if (!empty($property_type)) {
    if (empty($property_subypes)) {
        $extraUrlParams['property_type'] = [
            $property_type => [
                'all' => 'all',
            ],
        ];
    } else {
        $property_sub_types = \App\Connector\PropertySubTypes::getList();
        $extraUrlParams['property_type'] = [
            $property_type => [],
        ];

        foreach ($property_subypes as $value) {
            $extraUrlParams['property_type'][$property_type][$value] = $value;
        }
    }
}

foreach ($extraUrlParams as $k => $v) {
    if (empty($v)) {
        unset($extraUrlParams[$k]);
    }
}

//saved search flag variable.
$extraUrlParams['ssf'] = '1';
$url .= '?' . http_build_query($extraUrlParams);
?>
